mapper.update:
update notice set
notice_title=#{noticeTitle},
notice_content=#{noticeContent},
notice_thumb=#{noticeThumb}
where notice_no=#{noticeNo}

mapper.select:
select * from notice_file
where notice_file_no in

#{noticeFileNo}

mapper.delete:
delete from notice_file
where notice_file_no in

#{noticeFileNo}
