mapper.update:
update product set product_name = #{productName},
product_img = #{productImg},
product_content = #{productContent}, product_price = #{productPrice}, coronation = #{coronation}, dining_room = #{diningRoom}, number_people =#{numberPeople} where product_no =#{productNo}

mapper.delete:
delete from product_file
where file_path in

	#{file}

mapper.select:
SELECT
SUM(pay_price) AS sales
FROM (
SELECT pay_price
FROM member_pay
WHERE
SUBSTR(merchant_uid, 1, 4) = #{selectedYear}
AND SUBSTR(merchant_uid, 5, 2) = #{month}

UNION ALL

SELECT pay_price
FROM company_pay
WHERE
SUBSTR(merchant_uid, 1, 4) = #{selectedYear}
AND SUBSTR(merchant_uid, 5, 2) = #{month}
)

select * from (select rownum as rnum, a.* from( select distinct m.member_no, m.member_name, m.member_phone, m.member_gender, m.member_email, m.partner_name from product p
join member_pay mp on p.product_no = mp.product_no
join member m on m.member_no = mp.member_no
where p.product_no in
#{No}
) a) where rnum between #{pi.start} and#{pi.end}
